sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {




    var prb = new GlideRecord("sysapproval_approver");

    prb.addEncodedQuery("parent=" + current.sys_id);

    prb.query();

    if (!prb.next())

    {

        gs.addInfoMessage("You need to add the approval to the tab");

        action.setRedirectURL(current);

        current.setAbortAction(true);


    }



})(current, previous);
